mapper.select:
select s.*, c.name as categoryName from setmeal s left outer join category c on s.category_id = c.id
and s.name like concat('%', #{name},'%')
and s.category_id = #{categoryId}
and s.status = #{status}
order by s.create_time desc

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}, create_time = #{createTime}, create_user = #{createUser},
where id = #{id}
